function RPE.Data.DefaultClassic.Recipes()
    local items = {}
    local recipeTables = {
        BLACKSMITHING,
        TAILORING,
        LEATHERWORKING,
        ALCHEMY,
        INSCRIPTION,
        MINING,
        HERBALISM,
        ENGINEERING,
        ENCHANTING,
        JEWELCRAFTING,
        FIRST_AID,
        COOKING
    }
    
    for _, recipeTable in ipairs(recipeTables) do
        for k, v in pairs(recipeTable) do
            items[k] = v
        end
    end
    
    return items
end

return RPE.Data.DefaultClassic.Recipes()
